mapper.select:
SELECT

FROM sys_user a

WHERE a.id = #{id}

SELECT

FROM sys_user a

WHERE a.login_name = #{loginName} AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
FROM sys_user a
JOIN sys_user_role ur ON ur.user_id = a.id AND ur.role_id = #{role.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND (c.id = #{company.id} OR c.parent_ids LIKE '%'||#{company.id}||'%') CONCAT('%', #{company.id}, '%'))
AND (o.id = #{office.id} OR o.parent_ids LIKE '%'||#{office.id}||'%') CONCAT('%', #{office.id}, '%'))
AND o.name LIKE '%'||#{office.name}||'%' CONCAT('%', #{office.name}, '%')
AND a.id != '1'
AND a.login_name like '%'||#{loginName}||'%' CONCAT('%', #{loginName}, '%')
AND a.name like '%'||#{name}||'%' CONCAT('%', #{name}, '%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.name,o.name,a.login_name

SELECT


FROM sys_user a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.office_id = #{office.id}
ORDER BY a.name,c.code, o.code

mapper.update:
UPDATE sys_user SET 
	password = #{password} 
WHERE id = #{id}

UPDATE sys_user SET 
	login_ip = #{loginIp}, 
	login_Date = #{loginDate} 
WHERE id = #{id}

UPDATE sys_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select * from sys_menu t1 where t1.id in
(select t2.menu_id from sys_role_menu t2
	where t2.role_id in (
	select t3.role_id from sys_user_role t3
	where  t3.user_id = #{id}
	)
)
and t1. task_flag = '1'
and t1.del_flag = '0'
order by t1.sort

mapper.update:
update sys_user t
set password = #{password}
where t.password = '' or t.password is null
